package hospital.management.system;import net.proteanit.sql.DbUtils;import javax.swing.*;import java.awt.*;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;import java.sql.ResultSet;import java.util.ArrayList;public class ALL_Patient_Info extends JFrame {    ALL_Patient_Info() {        JPanel panel = new JPanel();        panel.setBounds(5, 5, 1000, 590);        panel.setBackground(new Color(90, 156, 163));        panel.setLayout(null);        add(panel);        JTable table = new JTable();        table.setBounds(10, 40, 1000, 450);        table.setBackground(new Color(90, 156, 163));        table.setFont(new Font("Tahoma", Font.BOLD, 12));        panel.add(table);        try {            conn c = new conn();            String q = "select * from Patient_Info";            ResultSet resultSet = c.statement.executeQuery(q);            table.setModel(DbUtils.resultSetToTableModel(resultSet));        } catch (Exception e) {            e.printStackTrace();        }        ArrayList<JLabel> labelList = new ArrayList<>();        String[] labels = {"ID", "Number", "Name", "Gender", "Disease", "Room", "Time", "Deposit"};        int xPos = 31;        for (String label : labels) {            JLabel lbl = new JLabel(label);            lbl.setBounds(xPos, 11, 100, 14);            lbl.setFont(new Font("Tahoma", Font.BOLD, 14));            panel.add(lbl);            labelList.add(lbl);            xPos += 120;        }        JButton button = new JButton("BACK");        button.setBounds(450, 510, 120, 30);        button.setBackground(Color.black);        button.setForeground(Color.white);        panel.add(button);        button.addActionListener(new ActionListener() {            @Override            public void actionPerformed(ActionEvent e) {                setVisible(false);            }        });        setSize(1000, 600);        setLayout(null);        setLocation(200, 100);        setVisible(true);    }    public static void main(String[] args) {        new ALL_Patient_Info();    }}